import React, { useState, useRef, useEffect } from 'react';
import './ChatWindow.css';

const ChatWindow = ({ messages, onSendMessage, isLoading, currentAgent, systemStatus }) => {
  const [inputMessage, setInputMessage] = useState('');
  const inputRef = useRef(null);

  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputMessage.trim() && !isLoading) {
      onSendMessage(inputMessage);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      handleSubmit(e);
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const getConfidenceClass = (confidence) => {
    if (confidence >= 0.8) return 'confidence-high';
    if (confidence >= 0.6) return 'confidence-medium';
    return 'confidence-low';
  };

  const getIntentClass = (confidence) => {
    if (confidence >= 0.8) return 'intent-high';
    if (confidence >= 0.6) return 'intent-medium';
    return 'intent-low';
  };

  const renderEntities = (entities) => {
    if (!entities || Object.keys(entities).length === 0) return null;
    
    const safeStringify = (value) => {
      if (value === null || value === undefined) return 'N/A';
      if (Array.isArray(value)) return value.join(', ');
      if (typeof value === 'object') {
        try {
          return JSON.stringify(value, null, 2);
        } catch (e) {
          return '[Complex Object]';
        }
      }
      return String(value);
    };
    
    return (
      <div className="entities-display">
        <strong>Entities Detected:</strong>
        <div className="entities-grid">
          {Object.entries(entities).map(([key, value]) => (
            <span key={key} className="entity-tag">
              {key}: {safeStringify(value)}
            </span>
          ))}
        </div>
      </div>
    );
  };

  const renderDataVisualization = (data) => {
    if (!data || typeof data !== 'object') return null;

    // Handle different data types
    if (data.type === 'voyage_plan') {
      return (
        <div className="data-visualization">
          <h4>🚢 Voyage Plan</h4>
          <div className="data-grid">
            <div className="data-item">
              <div className="data-item-label">Origin</div>
              <div className="data-item-value">{data.origin || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">Destination</div>
              <div className="data-item-value">{data.destination || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">Distance</div>
              <div className="data-item-value">{data.distance || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">ETA</div>
              <div className="data-item-value">{data.eta || 'N/A'}</div>
            </div>
          </div>
        </div>
      );
    }

    if (data.type === 'cargo_match') {
      return (
        <div className="data-visualization">
          <h4>📦 Cargo Match</h4>
          <div className="data-grid">
            <div className="data-item">
              <div className="data-item-label">Vessel</div>
              <div className="data-item-value">{data.vessel || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">Cargo</div>
              <div className="data-item-value">{data.cargo || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">Profitability</div>
              <div className="data-item-value">{data.profitability || 'N/A'}</div>
            </div>
          </div>
        </div>
      );
    }

    if (data.type === 'market_data') {
      return (
        <div className="data-visualization">
          <h4>📊 Market Data</h4>
          <div className="data-grid">
            <div className="data-item">
              <div className="data-item-label">Freight Rate</div>
              <div className="data-item-value">{data.freight_rate || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">Bunker Price</div>
              <div className="data-item-value">{data.bunker_price || 'N/A'}</div>
            </div>
            <div className="data-item">
              <div className="data-item-label">Trend</div>
              <div className="data-item-value">{data.trend || 'N/A'}</div>
            </div>
          </div>
        </div>
      );
    }

    // Generic data display
    return (
      <div className="message-data">
        <details>
          <summary>View Data</summary>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </details>
      </div>
    );
  };

  const renderMessage = (message) => {
    const isUser = message.sender === 'user';
    const confidence = message.confidence;
    const intentClass = confidence ? getIntentClass(confidence) : '';
    
    return (
      <div key={message.id} className={`message ${isUser ? 'user' : 'agent'} ${intentClass}`}>
        <div className="message-header">
          {!isUser && (
            <>
              <span className="message-agent" style={{ color: getAgentColor(message.agent) }}>
                {message.agent}
              </span>
              {confidence && (
                <span className={`confidence-indicator ${getConfidenceClass(confidence)}`}>
                  {Math.round(confidence * 100)}%
                </span>
              )}
            </>
          )}
          <span className="message-time">{formatTimestamp(message.timestamp)}</span>
        </div>
        
        <div className="message-text">{message.text}</div>
        
        {!isUser && message.entities && renderEntities(message.entities)}
        {!isUser && message.data && renderDataVisualization(message.data)}
      </div>
    );
  };

  const getAgentColor = (agentName) => {
    const agentColors = {
      'Captain Router': '#4A90E2',
      'Voyage Planner': '#7ED321',
      'Cargo Matcher': '#F5A623',
      'Market Insights': '#D0021B',
      'Port Intelligence': '#9013FE',
      'PDA Management': '#50E3C2'
    };
    return agentColors[agentName] || '#4A90E2';
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h2>🚢 Maritime AI Chat</h2>
        <div className="chat-status">
          {currentAgent && (
            <span>Active: <span style={{ color: getAgentColor(currentAgent) }}>{currentAgent}</span></span>
          )}
          <span className={`status-indicator ${systemStatus}`}></span>
          <span>{systemStatus === 'operational' ? '🟢 System OK' : 
                systemStatus === 'degraded' ? '🟡 System Degraded' : 
                systemStatus === 'error' ? '🔴 System Error' : '⚪ Connecting...'}</span>
        </div>
      </div>

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="welcome-message">
            <h3>🚢 Welcome to IME Hub Maritime AI Assistant!</h3>
            <p>I can help you with:</p>
            <ul>
              <li><strong>🧭 Voyage Planning:</strong> Route optimization, ETA calculations, fuel planning</li>
              <li><strong>📦 Cargo Matching:</strong> Vessel-cargo pairing and profitability analysis</li>
              <li><strong>📊 Market Insights:</strong> Freight rates, bunker prices, market trends</li>
              <li><strong>⚓ Port Intelligence:</strong> Port information and bunker availability</li>
              <li><strong>💰 PDA Management:</strong> Port disbursement cost estimates</li>
            </ul>
            <p>Try asking: "Plan a voyage from Singapore to Rotterdam using a Panamax vessel"</p>
          </div>
        ) : (
          messages.map(renderMessage)
        )}
        
        {isLoading && (
          <div className="loading-indicator">
            <div className="loading-spinner"></div>
            <span>AI is thinking...</span>
          </div>
        )}
      </div>

      <form className="chat-input-container" onSubmit={handleSubmit}>
        <textarea
          ref={inputRef}
          className="chat-input"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Ask about maritime operations, voyage planning, cargo matching..."
          disabled={isLoading}
          rows={2}
        />
        <button type="submit" className="send-button" disabled={isLoading || !inputMessage.trim()}>
          {isLoading ? '⏳' : '🚢'}
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
