const PROACTIVE_ALERTS = {
  weather: [
    {
      id: 1,
      type: 'weather',
      title: 'Storm Warning - Bay of Biscay',
      message: 'Storm forecast may delay vessel ETA by 36 hrs',
      severity: 'high',
