import React, { useState, useEffect, useRef } from 'react';
import ChatWindow from './components/ChatWindow';
import AgentAvatar from './components/AgentAvatar';
import './App.css';

const AGENTS = [
  {
    id: 'general',
    name: 'Captain Router',
    avatar: 'üë®‚Äç‚úàÔ∏è',
    description: 'Intelligent routing and general assistance',
    color: '#4A90E2',
    capabilities: ['Route optimization', 'Multi-agent coordination', 'Context awareness']
  },
  {
    id: 'voyage_planner',
    name: 'Voyage Planner',
    avatar: 'üß≠',
    description: 'Smart route planning with weather & optimization',
    color: '#7ED321',
    capabilities: ['Weather routing', 'Fuel optimization', 'What-if scenarios', 'ETA calculation']
  },
  {
    id: 'cargo_matching',
    name: 'Cargo Matcher',
    avatar: 'üì¶',
    description: 'Vessel-cargo pairing with profitability analysis',
    color: '#F5A623',
    capabilities: ['Cargo matching', 'Profitability estimates', 'Real-time tracking', 'Laycan optimization']
  },
  {
    id: 'market_insights',
    name: 'Market Insights',
    avatar: 'üìä',
    description: 'Freight rates, bunker trends & market analysis',
    color: '#D0021B',
    capabilities: ['Market trends', 'Freight rates', 'Bunker prices', 'Benchmarking']
  },
  {
    id: 'port_intelligence',
    name: 'Port Intelligence',
    avatar: '‚öì',
    description: 'Port data, bunker availability & costs',
    color: '#9013FE',
    capabilities: ['Port information', 'Bunker ports', 'Cost analysis', 'Availability tracking']
  },
  {
    id: 'pda_management',
    name: 'PDA Management',
    avatar: 'üí∞',
    description: 'Port disbursement & cost management',
    color: '#50E3C2',
    capabilities: ['PDA estimates', 'Cost tracking', 'Budget management', 'Variance analysis']
  }
];

// Pre-defined maritime queries for quick access
const QUICK_QUERIES = [
  {
    text: "Plan voyage from Singapore to Rotterdam",
    agent: "Voyage Planner",
    description: "Route optimization with weather"
  },
  {
    text: "Find cargo for Panamax vessel",
    agent: "Cargo Matcher", 
    description: "Cargo matching & profitability"
  },
  {
    text: "Current bunker prices in major ports",
    agent: "Market Insights",
    description: "Market analysis & trends"
  },
  {
    text: "Best bunker ports en route to Suez",
    agent: "Port Intelligence",
    description: "Port optimization"
  },
  {
    text: "Calculate PDA for Shanghai port call",
    agent: "PDA Management",
    description: "Cost estimation & tracking"
  }
];

function App() {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentAgent, setCurrentAgent] = useState(null);
  const [systemStatus, setSystemStatus] = useState('connecting');
  const [apiStatus, setApiStatus] = useState({});
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Check system status on load
    checkSystemStatus();
    // Add welcome message
    addWelcomeMessage();
  }, []);

  const checkSystemStatus = async () => {
    try {
      const response = await fetch('/api/status');
      const data = await response.json();
      setSystemStatus(data.status);
      setApiStatus(data.apis || {});
    } catch (error) {
      setSystemStatus('error');
      console.error('System status check failed:', error);
    }
  };

  const addWelcomeMessage = () => {
    const welcomeMessage = {
      id: Date.now(),
      text: "üö¢ Welcome to IME Hub Maritime AI Assistant! I'm your intelligent maritime operations companion. I can help you with:\n\n" +
            "‚Ä¢ üß≠ **Smart Voyage Planning** - Route optimization, weather routing, fuel optimization\n" +
            "‚Ä¢ üì¶ **Cargo Matching** - Vessel-cargo pairing with profitability analysis\n" +
            "‚Ä¢ üìä **Market Insights** - Freight rates, bunker trends, market analysis\n" +
            "‚Ä¢ ‚öì **Port Intelligence** - Port data, bunker availability, cost analysis\n" +
            "‚Ä¢ üí∞ **PDA Management** - Port disbursement estimates and cost tracking\n\n" +
            "Try asking me about voyage planning, cargo matching, or any maritime operation!",
      sender: 'agent',
      agent: 'Captain Router',
      data: { type: 'welcome', capabilities: AGENTS.map(a => a.name) },
      timestamp: new Date().toISOString()
    };
    setMessages([welcomeMessage]);
  };

  const sendMessage = async (message, useContext = true) => {
    if (!message.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: message,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: message,
          user_id: 'default',
          use_context: useContext,
          timestamp: new Date().toISOString()
        }),
      });

      const data = await response.json();
      
      if (data.status === 'success') {
        const agentMessage = {
          id: Date.now() + 1,
          text: data.text,
          sender: 'agent',
          agent: data.agent,
          data: data.data,
          intent: data.intent,
          confidence: data.confidence,
          entities: data.entities,
          timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, agentMessage]);
        setCurrentAgent(data.agent);
      } else {
        const errorMessage = {
          id: Date.now() + 1,
          text: data.text || 'An error occurred. Please try again.',
          sender: 'agent',
          agent: 'Captain Router',
          data: data.data,
          timestamp: new Date().toISOString()
        };

        setMessages(prev => [...prev, errorMessage]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Sorry, I encountered an error. Please try again.',
        sender: 'agent',
        agent: 'Captain Router',
        data: { error: error.message },
        timestamp: new Date().toISOString()
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleQuickQuery = (query) => {
    sendMessage(query.text);
    setCurrentAgent(query.agent);
  };

  const clearChat = () => {
    setMessages([]);
    setCurrentAgent(null);
    addWelcomeMessage();
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'operational': return '#7ED321';
      case 'degraded': return '#F5A623';
      case 'error': return '#D0021B';
      default: return '#9B9B9B';
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üö¢ IME Hub Maritime AI Assistant</h1>
        <p>Phase 3: Advanced Maritime AI with Real-time Data & LLM Reasoning</p>
        <div className="system-status">
          <span className={`status-indicator ${systemStatus}`}></span>
          <span className="status-text">
            System: {systemStatus === 'operational' ? 'üü¢ Operational' : 
                    systemStatus === 'degraded' ? 'üü° Degraded' : 
                    systemStatus === 'error' ? 'üî¥ Error' : '‚ö™ Connecting...'}
          </span>
        </div>
      </header>

      <div className="App-container">
        <div className="agents-sidebar">
          <h3>ü§ñ AI Agents</h3>
          <div className="agents-grid">
            {AGENTS.map(agent => (
              <AgentAvatar
                key={agent.id}
                agent={agent}
                isActive={currentAgent === agent.name}
                onClick={() => setCurrentAgent(agent.name)}
              />
            ))}
          </div>
          
          <div className="quick-queries">
            <h4>üöÄ Quick Queries</h4>
            {QUICK_QUERIES.map((query, index) => (
              <button
                key={index}
                className="quick-query-btn"
                onClick={() => handleQuickQuery(query)}
                title={query.description}
              >
                {query.text}
              </button>
            ))}
          </div>

          <button className="clear-chat-btn" onClick={clearChat}>
            üóëÔ∏è Clear Chat
          </button>
        </div>

        <div className="chat-main">
          <ChatWindow
            messages={messages}
            onSendMessage={sendMessage}
            isLoading={isLoading}
            currentAgent={currentAgent}
            systemStatus={systemStatus}
          />
        </div>
      </div>

      <div ref={messagesEndRef} />
    </div>
  );
}

export default App;
